import React from 'react';
import PropTypes from 'prop-types';
import { Card, Button, Popover, ButtonGroup } from 'antd';
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';

const PostCard = ({ post }) => {
    const { me } = useSelector((state) => state.user);
    return (
        <div>
            <Card
                cover={post.Image[0] && <PostImages images={post.Images} />}
                // 배열안에 jsx를 넣을때는 꼭 key를 넣어주기!
                actions={[
                    <RetweetOutlined key="retweet" />,
                    <HeartOutlined key="heart" />,
                    <MessageOutlined key="comment" />,
                    <Popover key="more" content={(
                        <ButtonGroup>
                            <Button>수정</Button>
                            <Button type="danger">삭제</Button>
                            <Button>신고</Button>
                        </ButtonGroup>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                    
                ]}
            >
                <Image />
                <Content />
                <Button></Button>
            </Card>
            <CommentForm />
            <Comments />
        </div>
    );
};

export default PostCard;
